// Analisis pesan masuk dari WhatsApp
const message = $json;
const text = message.text.toLowerCase().trim();
const isAdmin = message.isAdmin || false;

// Deteksi jenis permintaan
let messageType = 'general';
let response = {};

// Cek apakah pesan tentang sholat
if (text.includes('sholat') || text.includes('solat') || 
    text.includes('maghrib') || text.includes('subuh') || 
    text.includes('dzuhur') || text.includes('ashar') || 
    text.includes('isya') || text.includes('jadwal')) {
  messageType = 'prayer_schedule';

  // Extract city if mentioned
  const cityKeywords = ['jakarta', 'surabaya', 'bandung', 'medan', 'semarang', 'yogyakarta', 'malang', 'solo', 'depok', 'bekasi'];
  let city = process.env.DEFAULT_CITY || 'Jakarta';

  for (const keyword of cityKeywords) {
    if (text.includes(keyword)) {
      city = keyword.charAt(0).toUpperCase() + keyword.slice(1);
      break;
    }
  }

  response = {
    type: 'prayer_schedule',
    city: city,
    originalMessage: message.text
  };
}
// Cek apakah pesan broadcast dari admin
else if ((text.startsWith('broadcast:') || text.startsWith('/broadcast')) && isAdmin) {
  messageType = 'admin_broadcast';
  const broadcastMessage = text.replace(/^(broadcast:|/broadcast)\s*/, '');

  response = {
    type: 'admin_broadcast',
    message: broadcastMessage,
    adminNumber: message.number
  };
}
// Cek apakah pesan command admin
else if (text.startsWith('/') && isAdmin) {
  messageType = 'admin_command';
  const command = text.substring(1).split(' ')[0];

  response = {
    type: 'admin_command',
    command: command,
    adminNumber: message.number,
    fullText: text
  };
}
// Pesan umum untuk AI
else {
  messageType = 'ai_chat';
  response = {
    type: 'ai_chat',
    question: message.text,
    context: 'masjid'
  };
}

// Return hasil analisis
return {
  ...message,
  messageType: messageType,
  analysis: response
};

// Process Aladhan API response untuk jadwal sholat
const response = $json;
const originalRequest = $input.first().json;

let cityName = originalRequest.analysis.city;
let prayerData = null;

if (response.code === 200 && response.data && response.data.timings) {
  // Response dari Aladhan API berhasil
  prayerData = {
    success: true,
    source: 'aladhan',
    city: response.data.meta.timezone || cityName,
    date: response.data.date.readable,
    timings: response.data.timings
  };
} else {
  // Jika Aladhan gagal, return error untuk fallback ke MyQuran API
  prayerData = {
    success: false,
    source: 'aladhan',
    city: cityName,
    error: 'Aladhan API failed'
  };
}

return {
  ...prayerData,
  originalRequest: originalRequest
};

// Format jadwal sholat untuk WhatsApp
const prayerData = $input.first().json;
const originalRequest = prayerData.originalRequest;

let replyMessage = '';

if (prayerData.success && prayerData.timings) {
  const timings = prayerData.timings;
  const cityName = prayerData.city;
  const date = prayerData.date;

  replyMessage = `üïå *Jadwal Sholat ${cityName}*\n`;
  replyMessage += `üìÖ ${date}\n\n`;

  // Format waktu dari Aladhan API
  if (prayerData.source === 'aladhan') {
    replyMessage += `üåÖ Subuh: ${timings.Fajr}\n`;
    replyMessage += `üåû Dzuhur: ${timings.Dhuhr}\n`;
    replyMessage += `üå§Ô∏è Ashar: ${timings.Asr}\n`;
    replyMessage += `üåÖ Maghrib: ${timings.Maghrib}\n`;
    replyMessage += `üåô Isya: ${timings.Isha}\n\n`;
  } else {
    // Format untuk MyQuran API v2 (jika berbeda)
    replyMessage += `üåÖ Subuh: ${timings.subuh || timings.Fajr}\n`;
    replyMessage += `üåû Dzuhur: ${timings.dzuhur || timings.Dhuhr}\n`;
    replyMessage += `üå§Ô∏è Ashar: ${timings.ashar || timings.Asr}\n`;
    replyMessage += `üåÖ Maghrib: ${timings.maghrib || timings.Maghrib}\n`;
    replyMessage += `üåô Isya: ${timings.isya || timings.Isha}\n\n`;
  }

  replyMessage += `ü§≤ Semoga Allah memudahkan ibadah kita\n`;
  replyMessage += `üì° _Sumber: ${prayerData.source === 'aladhan' ? 'Aladhan API' : 'MyQuran API'}_`;
} else {
  replyMessage = 'üôè Maaf, jadwal sholat tidak dapat diambil saat ini. Silakan coba lagi nanti.';
}

return {
  reply: replyMessage,
  number: originalRequest.number
};

// Format response dari AI
const response = $json;
const originalRequest = $input.first().json;

let replyMessage = '';

if (response.choices && response.choices.length > 0) {
  replyMessage = response.choices[0].message.content;

  // Add Islamic greeting if not present
  if (!replyMessage.includes('ÿßŸÑÿ≥ŸÑÿßŸÖ') && !replyMessage.includes('Assalamu')) {
    replyMessage = 'ü§ù Wa\'alaikumussalam warahmatullahi wabarakatuh\n\n' + replyMessage;
  }

  // Add footer
  replyMessage += '\n\nüïå _Bot Masjid AI - Melayani dengan hati_';
} else {
  replyMessage = 'üôè Maaf, saya tidak dapat memproses pertanyaan Anda saat ini. Silakan coba lagi nanti.';
}

return {
  reply: replyMessage,
  number: originalRequest.number
};

// Handle admin broadcast
const data = $json;
const broadcastMessage = data.analysis.message;

// Format broadcast message
const formattedMessage = `üì¢ *PENGUMUMAN MASJID*\n\n${broadcastMessage}\n\nüïå _Pengurus Masjid_`;

// Return broadcast data
return {
  type: 'broadcast',
  payload: {
    message: formattedMessage,
    excludeNumbers: [data.analysis.adminNumber] // Exclude admin from broadcast
  },
  adminConfirmation: {
    reply: `‚úÖ Broadcast berhasil dikirim!\n\nPesan: ${broadcastMessage}`,
    number: data.number
  }
};

// Return admin confirmation
const broadcastData = $input.first().json;

return {
  reply: broadcastData.adminConfirmation.reply,
  number: broadcastData.adminConfirmation.number
};

// Handle fallback for unrecognized messages
const data = $json;

let replyMessage = '';

if (data.messageType === 'admin_command') {
  replyMessage = `‚ùå Command tidak dikenali: ${data.analysis.command}\n\n` +
    `üìã Command yang tersedia:\n` +
    `‚Ä¢ /broadcast [pesan] - Kirim pengumuman\n` +
    `‚Ä¢ /status - Cek status bot\n` +
    `‚Ä¢ /stats - Lihat statistik`;
} else {
  replyMessage = `ü§ñ Halo! Saya adalah Bot Masjid AI.\n\n` +
    `üïå Saya bisa membantu Anda dengan:\n` +
    `‚Ä¢ Jadwal sholat (ketik: jadwal sholat)\n` +
    `‚Ä¢ Pertanyaan seputar Islam\n` +
    `‚Ä¢ Informasi kegiatan masjid\n\n` +
    `üí¨ Silakan ajukan pertanyaan Anda!`;
}

return {
  reply: replyMessage,
  number: data.number
};
